void mainImage( out vec4 a, vec2 b )
{
    a -= a;
	b /= iResolution.xy;

    for(a.a = .56; a.a < 4. ; a.a += .21)
    {
       vec2 d = ( b - .5 ) * ( cos( a.a * iTime * .9 ) + 1. ) + .1 + a.a;
       d = vec2(d.x*cos(a.a*1.1)-d.y*sin(a.a),d.y*cos(a.a)+d.x*sin(a.a));
	   a += step(
           texture( iChannel0, d) ,
           vec4( (sin(iTime)+1.)/1e2 )
        ).gbra*.7;
    }

    a = tanh(a+a.brga*.3);
}
