void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

bool getCell(vec2 pos) {
    return texture(iChannel0, pos.xy / iResolution.xy).r > 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    if (distance(iMouse.xy, fragCoord.xy) <= 1.0) {
        fragColor = vec4(1.0);
		return;
    }


    vec2 uv = fragCoord.xy / iResolution.xy;

    int cnt = 0;
    for (int i = -1; i < 2; i++) {
        for (int j = -1; j < 2; j++) {
            if (i != 0 || j != 0) {
                if (getCell(fragCoord.xy + vec2(i, j))) 
            		cnt++;
            }
        }
    }

    bool willLive = false;
    if (cnt == 3) {
        willLive = true;
    } else {
        bool aliveQ = getCell(fragCoord.xy);
        if (aliveQ && cnt == 2) {
            willLive = true;
        }
    }

    if (willLive) {
    	fragColor = vec4(1.0);
    } else {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
}
