#define M_PI 3.1415926535897932384626433832795
#define PARTS 100.
#define WIDTH 0.1


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;
    uv = 2. * uv - 1.;

    float SPIRAL_GAP = WIDTH / PARTS;
    float ANGLE = 360. / PARTS;

    float dist_to_point = distance(uv, vec2(0., 0.));

    float theta = asin(uv.y / dist_to_point);

    theta *= (180. / M_PI);

    if (theta >= 0.)
    {
        if (uv.x >= 0.)
            ;
        else
            theta = 180. - theta;
    }
    else 
    {
        if (uv.x >= 0.) 
            theta += 360.;
        else
            theta = 180. - theta;
    }


    float group = floor(theta / ANGLE);
    float group_radius = group * SPIRAL_GAP;
    float time_variation = mod(iTime, 0.9) / 9.;

    float remainder = mod(dist_to_point, WIDTH);

    group_radius += time_variation;
    if (group_radius > WIDTH)
    {
        remainder += WIDTH;
    }

    float final_color = 0.;

    final_color = smoothstep(group_radius - 0.007, group_radius + 0.005, remainder);
    if (final_color == 1.)
        final_color = 0.;
    fragColor = vec4(vec3(final_color), 1.);
}
