#define sin01(x) (.5+.5*sin(x))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 ouv = uv;

    float v = smoothstep(2.,9.,iTime)*.44;

    float cs = iTime < 7. ? 0. : .6+.1*sin(.2*iTime);

    if(abs(uv.y-.5) < v)
    {
        uv += .001*cs*cos(.4*iTime+iTime*vec2(2,3));

        uv *= .9;
        uv +=.03;

        vec4 blur = textureLod(iChannel0,uv,7.5);

        blur = (blur-.6)*6.;
        blur = max(blur,vec4(0));
        float bloom = length(blur.xyz);

        bloom *= bloom*bloom/2.;

        vec4 tex = texture(iChannel0,uv);

        float vn = 1.-1.6*length(ouv-.5);

        fragColor = tex+.1*tex*bloom;
        fragColor = pow(fragColor,vec4(.8));
        fragColor *= vn;
    }
    else
        fragColor = vec4(0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float t = .4*iTime-2.8;
    float s = sin(t/16.), c = cos(t/16.);
    vec2 muv = fragCoord/iResolution.xy-.5;
    muv += .01*sin(muv.yx*vec2(10.5,8.3)+t/8.);
    muv *= mat2(s,c,c,-s);
    float sc = 3.5+.5*sin(iTime*.4);
    muv *= smoothstep(0.,12.,t)*sc;
    muv = abs(muv);
    muv = fract(abs(muv-1.));
    float n = sin(t/2.)*.2+.8;
    vec4 last = texture(iChannel0,muv*n);
    vec4 new = .1/length(muv-vec2(.4+.2*s,.5+.05*c))*vec4(uv,1,1);
    fragColor = (last*min(.75,t/15.)-.1)+new;
}
