vec4 T( vec2 u )
{
    u += iTime/vec2(49,5);
    return pow(1.-texture(iChannel0,u).x,3.)
        	+ texture(iChannel1,u*1.3)*.1;
}

void mainImage( out vec4 o, vec2 s )
{
    s = s/iResolution.y-vec2(.5,-.8);
    s /= 18.-s.y/.1;

    for ( float i=1.; i > 0.; i -= .002 )
        o = T( o.x<i ? s /= .9987 : s );

    o = exp2(-s.y*.3)*(o*9.5-T(s+.001).x*9.-.7)+.9;
}
