/* based on my shader : https://www.shadertoy.com/view/XlSSzV 
just use df based on cos, sin instead of iq 3d noise */

void mainImage( out vec4 f, vec2 g )
{
    vec3 
        s = iResolution,
        r = vec3(g+g-s.xy,s.y),
    	k=s,//+iTime,
        q;

    k.z = iTime;

	float d,u,c,i;

    for(d=u=i=0.;i<1.;i+=.004)
	{      
        // the normalize here is costly but just for have less chars :)
		q = k + r/length(r) * d * .3;

        // from shane but formula used also in sculpture III :) 
        c = length(cos(q+sin(q.zxy*3.)*.5)) - 1.;
        // give a sort of latice :) : c = length(cos(q.zxy+sin(q.zxy*3.)*.5)) - 1.;

        if(u>.9) break;

        // better if (c < .1) u += (1.-u) * (.1-c) * i;
        if (c < .1) u += (.1-c) * i; 					

        u += .005; 										

        d += max(c * .6, .05);							
   	}

	f -= f - exp(-d*d/1e3)/i/7.;
}
