float inMandelBrot(vec2 c){
    vec2 z = vec2(0,0);
    for(float i = 0.; i < 100.; i++){
        z = vec2(z.x*z.x-z.y*z.y+c.x,2.*z.x*z.y+c.y);
        if(z.x > 2.)
            return i;
    }
    return 0.;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = vec2(0,0);
    coord = vec2((fragCoord.x-iResolution.x/2.)/iResolution.y*4.,(fragCoord.y-iResolution.y/2.)/iResolution.y*4.);
	float temp = inMandelBrot(coord);
    fragColor = vec4(temp/16.,temp/16.,0,1);	
}
