float level = 0.0;
vec2 uv = vec2(0.0);
#define pi 3.1415926

float rfloat(float juice) {
    return fract(sin(juice*1.06)*42788.5251);
}

void circleoutline(vec2 center, float radius) {
    float dist = length(uv - center);
    float width = 2.0/iResolution.y;

    if (dist > radius-width && dist < radius+width) {
        level = min(1.0,(cos((radius-dist)*pi/width) + 1.)/2. +level);
    }
}

void circle(vec2 v, vec2 cent, float rad, float alpha) {
    if (length(v-cent) < rad) {
        level = level*(1.-alpha) + alpha;
    }    
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float x = (fragCoord.x - (iResolution.x/2.))*1./iResolution.y;
    float y = (fragCoord.y - (iResolution.y/2.))*1./iResolution.y;
    uv = vec2(x,y);
    circle(uv, vec2(0.0), 0.2, 1.0);

    float alpha = level;
    level=0.0;
    vec3 finalcol = vec3(0.0,0.0,0.0);
    vec3 glowcolour = vec3(0.8,0.7,0.0);
    vec3 ilcol = vec3(0.25,0.25,0.25);

    circleoutline(vec2(0.0),0.2);
    finalcol += level*vec3(0.9);
    alpha = min(alpha + level,1.0);
    level = 0.0;

    for(float i=0.;i<4.;i++) {
        level = 0.0;
        float rad = 0.04 + 0.035*i;
        circleoutline(vec2(0.0),rad);

        float glowlevel = 0.;
        float angle = iTime*4.*(0.75+rfloat(i*1.1)) + rfloat(i*1.2);
        vec2 glowPoint = vec2(rad*cos(angle),rad*sin(angle));
        float dist = length(uv - glowPoint);
        if (dist < 0.1) {
            glowlevel = min(1.,glowlevel + (1. - dist/0.1));
        }

        finalcol += level*ilcol*(1.-glowlevel) + level*glowlevel*glowcolour;
    }

    float glowlevel = 0.0;
    fragColor = vec4(finalcol*alpha,1.0);

    float scale = 0.2;
}
