const float PI = 3.141592653589793;
const float DEEP_COLOR_RATE = 0.8;

vec4 hsv_to_rgb(float h, float s, float v, float a)
{
	float c = v * s;
	h = mod((h * 6.0), 6.0);
	float x = c * (1.0 - abs(mod(h, 2.0) - 1.0));
	vec4 color;

	if (0.0 <= h && h < 1.0) {
		color = vec4(c, x, 0.0, a);
	} else if (1.0 <= h && h < 2.0) {
		color = vec4(x, c, 0.0, a);
	} else if (2.0 <= h && h < 3.0) {
		color = vec4(0.0, c, x, a);
	} else if (3.0 <= h && h < 4.0) {
		color = vec4(0.0, x, c, a);
	} else if (4.0 <= h && h < 5.0) {
		color = vec4(x, 0.0, c, a);
	} else if (5.0 <= h && h < 6.0) {
		color = vec4(c, 0.0, x, a);
	} else {
		color = vec4(0.0, 0.0, 0.0, a);
	}

	color.rgb += v - c;

	return color;
}

float get_rate(float n){
	return n * PI;
}

float myfuncX(float n, float _time){
	float rate = n*sin(_time);
	return (abs(2.0*cos(rate)*sin(3.0*rate)) + abs(sin(cos(2.0*rate)))) / 2.0;
}

float myfuncY(float n, float _time){
	float rate = n*cos(_time);
	return (abs(sin(rate)*1.5*cos(2.6*rate)) + abs(sin(-sin(2.0*rate)))) / 2.0;
}

float myconv(float x, float y, float width, float height, float _time){
	float mil = _time*3.0;
	return myfuncX(get_rate(x/width), mil) * myfuncY(get_rate(y/height), mil);
}

vec4 get_color(float width, float height, float n, float _time, vec2 fragCoord){
	float m = _time*0.5;
	return hsv_to_rgb(m, 1.0, n, ((width - fragCoord.x)/width)+0.6);
}

vec4 get_depth(float x, float y, float width, float height, float millis, vec2 fragCoord){
	return get_color(width, height, myconv(x,y,width,height,millis), millis,fragCoord);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    float width = iResolution.x;
	float height = iResolution.y;
	float ms = iTime * 0.06;

	float x = fragCoord.x;
	float y = fragCoord.y;

	fragColor = get_depth(x, y, width, height, ms,fragCoord);
}
