#define time (iTime/20.)
#define ITERS 6

float hash(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.98928,78.233))) * 43758.5453);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 old = uv;
    uv.y += time;
    uv.x += time;

    vec3 col = vec3(0.);
    for(int i = 0; i < ITERS; i++){
        float g = hash(floor(uv.xy*(120.-float(i)*40.)));
        if (g >.95){
    		col = vec3(1.);
        	col -= (.9-float(i)/float(ITERS));
        }
   		uv.y += time;
    	uv.x += time*(1.+float(i));
    }

    col +=.95;
    float z = smoothstep(0.4, 1.0, old.y);
    col -= z / 5.;
    col.b = 1.;


	float v = smoothstep(0.4, 0.0, old.y);
    col += v;

	fragColor = vec4(col,1.0);
}
