void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 div = vec2( 16, 16.*iResolution.y/iResolution.x );
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 xy = div*uv;
    vec2 v = fract( xy );
    vec2 g = floor( xy );

    float c = mod(g.x + g.y, 2.0 );

	fragColor = vec4(c, c, c, 1.0);
}
