void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 img2 = texture(iChannel2,uv*0.2);
    vec2 fu = vec2(uv.x,uv.y);
    vec4 displace = texture(iChannel0,fu*0.137);
    float t = iTime * 0.5;
    float yo = sin(t)*0.3;
    float a = (sin(123.0+t*2.0)+1.0)*1.5;
    float one = cos(t) * (displace.x-0.5) * a;
    float two = sin(t) * (displace.x-0.5) * 0.3;
    vec2 tc = vec2(uv.x + one + img2.r*yo, uv.y - two);
    vec4 img = texture(iChannel1,tc);
    vec4 img3 = texture(iChannel1,uv);  
    img.r = mod(uv.y*iResolution.y,2.0);
    float g = (img.r+img.g+img.b)/3.0;
	fragColor = vec4((img3.r+g)*0.5,g,g,1.0);
}
