#define M_PI 3.1415926535897932384626433832795
#define M_TWO_PI (M_PI * 2.0)

vec3 hash(vec3 p3)
{    
	p3 = fract(p3 * vec3(.1031,.11369,.13787));
    p3 += dot(p3, p3.yxx+19.19);
    return -1.0 + 2.0 * fract(vec3((p3.x + p3.y)*p3.z, (p3.x+p3.z)*p3.y, (p3.y+p3.z)*p3.x));
}


float noise(vec2 n, float t) {

    vec3 p = vec3(n.x, n.y, t);
    const float K1 = 0.333333333;
    const float K2 = 0.166666667;

    vec3 i = floor(p + (p.x + p.y + p.z) * K1);
    vec3 d0 = p - (i - (i.x + i.y + i.z) * K2);

    vec3 e = step(vec3(0.0), d0 - d0.yzx);
	vec3 i1 = e * (1.0 - e.zxy);
	vec3 i2 = 1.0 - e.zxy * (1.0 - e);

    vec3 d1 = d0 - (i1 - 1.0 * K2);
    vec3 d2 = d0 - (i2 - 2.0 * K2);
    vec3 d3 = d0 - (1.0 - 3.0 * K2);

    vec4 h = max(0.6 - vec4(dot(d0, d0), dot(d1, d1), dot(d2, d2), dot(d3, d3)), 0.0);
    vec4 q = h * h * h * h * vec4(dot(d0, hash(i)), dot(d1, hash(i + i1)), dot(d2, hash(i + i2)), dot(d3, hash(i + 1.0)));

    return dot(vec4(50.), q);

}


float fbm(vec2 p, float time, float scale)
{
    float f = 0.0;
    float a = 1.0;
    p.xy *= scale;

    for (int i = 0; i < 6; i++) {
	    f += a * abs(noise(p, time)); 
        p = 2.0 * p;
        a /= 2.0;
    }

    return 1.0 - f;
}


vec4 shade(vec2 uv) {

    if(uv.x < 0.0 || uv.y < 0.0 || uv.x > 1.0 || uv.y > 1.0)
        return vec4(0.0);

    uv.y += iTime / 2.0;

    float f = fbm(uv * 12.0, iTime / 2.0, -0.5 + iMouse.x / iResolution.x);

    return vec4(f, f, f, 1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.yy;

	fragColor = shade(uv);
}
