void mainImage( out vec4 c, vec2 p ) {

    vec3 h, n = vec3(p/iResolution.x-.5, 1);

    //if (iMouse.z > .5) n.xy += (iMouse.xy-iResolution.xy*.5)*.003;

    float d, s, i, j, t=i=0.;
    for(;i++<2e2;) {

        h = n*t;
        h.z += iTime;

        d = 9e9;
        s = 2.;

        for(j=-.5;j++<8.;)
            d = min(d, max((length(h.xz = 2.*fract(h.xz-.3)-1.)-.2)/(s *= 2.), h.y+j*.1));

        t += d;
    }

    c = vec4(.9, .6, .4, 1) / t * cos(h.y*4.)*.7+.5;

}
