#define PI 3.14159265

const float a = 5.0;
const float b = 4.0;
const float w = PI / 2.0;
vec2 lissajous() {
    float t = iTime/10.0+222.19;
    float x = sin(a * t + w);
    float y = sin(b * t);
	return vec2(x, y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float xmin = -2.0;
    float ymin = -1.4;
    float xmax = 2.0;
    float ymax = 1.4;
    float xDelta;
    float yDelta;
    xDelta = (xmax - xmin) / iResolution.x;
    yDelta = (ymax - ymin) / iResolution.y;
    int iteration = 0;
    int max_iteration = 300;
    vec2 l = lissajous();
    float x = xmin + fragCoord.x * xDelta;
    float y = ymin + fragCoord.y * yDelta;
    while (x*x + y*y < 64.0 && iteration < max_iteration) {
        // Multiplication of complex numbers:
        // (a+bi) (c+di) = (ac-bd) + (bc+ad)i
        // c^2 = (x+yi) (x+yi) = (xx-yy) + (yx+yx)i = (xx-yy) + (2xy)i
        float xtemp = x * x - y * y + l.x;
        y = 2.0 * x * y + l.y;
        x = xtemp;
        iteration++;
    }        
    float c = 1.0 - float(iteration) / float(max_iteration)*6.0;
    fragColor = vec4(c, c, c, 1.0);
}
