#define CELLSIZE 0.02;
#define TANPI6 0.57735026919
#define PI2 1.57079632679

float squares(vec2 p)
{
    p /= CELLSIZE;
    float xy = floor(p.x) + floor(p.y);
    return mod(xy, 2.);
}

float triangles(vec2 p)
{
    p /= CELLSIZE;
    float offset = mod(floor(p.y), 2.) * TANPI6;
    p.x += offset;
    return 1. - step(0., TANPI6 - TANPI6*fract(p.y) - abs(mod(p.x, 2.*TANPI6) - TANPI6));
}

vec2 rotate(vec2 p, float a)
{	
    return vec2(cos(a)*p.x + sin(a)*p.y, -sin(a)*p.x + cos(a)*p.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (fragCoord - 0.5*iResolution.xy) / iResolution.y;
    float t = iTime/5.;

    float scale = 0.96;
    vec2 p2 = rotate(scale*p, t);
    float map = (mod(t/PI2, 2.) < 1.) ? triangles(p) * triangles(p2)
        							  : squares(p) * squares(p2);

    vec3 c1 = mix(vec3(0., 0., 0.8), vec3(0.8, 0., 0.), 0.5 + sin(2.*t)/2.);
    vec3 c2 = mix(vec3(0., 0.8, 0.), vec3(0.8, 0.8, 0.), 0.5 + sin(3.*t)/2.);
    vec3 col = mix(c1, c2, map);
	fragColor = vec4(vec3(col), 1.0);
}
