void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float line_width=2.;

    vec2 uv = fragCoord/iResolution.xy;

    float line1 =
        smoothstep(line_width/iResolution.y,0.,
                   abs((uv.y-.5)*4. - sin(4.0 * (uv.x + mod(0.5 * iTime + sin(iTime),3.14))))/4.);
	float line2 =
        smoothstep(line_width/iResolution.x,0.,
                   abs((uv.x-.5)*4. - sin(4.0 * (uv.y + mod(0.5 * iTime + sin(iTime),3.14))))/4.);

    vec3 col = (vec3(0));

    col = mix(vec3(0),
                  0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)),
                  line1+line2);

    fragColor = vec4(col,1.0);
}
