void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  fragColor = sin(
    reflect(
      vec4(
        (fragCoord.y / iResolution.y),
        iTime,
        (fragCoord.y / iResolution.y),
        1.1
      ),
      atan(
        vec4(
          reflect(iTime, (iTime * 0.4)),
          (1.3 * 1.6),
          iTime,
          1.1
        )
      )
    )
  );
}
