// line 34: pixel coordinates	
// line 14: c travels around the user defined cardiod
// line 25: z = zÂ² + c		
// line 26: trap orbit
// line 45: remap	
// line 46: color	
// line 47: split screen

vec2 offset( float t )
{
    return vec2( 0.0, 0.0 );
    //return vec2( cos(iTime), sin(iTime) );
}

float orbit( vec2 z, vec2 bias )
{
	float f = 1e20; // infinity
	for( int i=0; i<128; i++ ) 
	{
		z = vec2( z.x*z.x-z.y*z.y, 2.0*z.x*z.y ) + bias;
		f = min( f, dot(z,z) );
	}
    return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float ar = 4./3.; // aspect ratio
	vec2 p = ar*(-iResolution.xy+2.0*fragCoord.xy)/iResolution.y;
	vec2 m = ar*(-iResolution.xy+2.0*iMouse.xy   )/iResolution.y;

	float f;

    // Right Half = Static, Time=0
//    if( p.x > m.x )
//        f = orbit( p, offset( 0.0 ) );
//    else
        f = orbit( p, m + offset( iTime ) );

    f = 1.0+log(f)/16.0;
    vec3 color = vec3( f, f*f*f, f*f ); // changed f^1, f^3, f^2 for nice pink :)
    color *= smoothstep( 0.006, 0.008, abs(p.x-m.x) );
    color *= smoothstep( 0.006, 0.008, abs(p.y-m.y) );

	fragColor = vec4(color,1.0);
}

