void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ((fragCoord.xy / iResolution.xy) - vec2(0, 1)) * vec2(480., -320.);

    float fx = uv.x;
    float fy = uv.y;
    float time = iTime;

    float c_mul = 16.5 + sin(time / 2.0) * 15.5;

    float v =
        sin(distance(vec2(fx + time, fy), vec2(128.0 * sin(time), 128.0 * sin(time))) / 8.0) +
        sin(distance(vec2(fx, fy), vec2(64.0 * cos(time), 64.0 * cos(time))) / 16.0) +
        sin(distance(vec2(fx, fy + time / 7.0), vec2(192.0, 64.0)) / 17.0) +
        sin(distance(vec2(fx, fy), vec2(192.0 + 200.0 + sin(time) * 200.0, 100.0 + 200.0 + sin(time / 2.0) * 200.0)) / 8.0);

    float c = (floor((4.0 + v) * (32.0 / c_mul)) * c_mul);

	fragColor = vec4(
        c / 128.,
        c / 256.,
        (255. - c) / 256.,
    	1.0);
}
