void mainImage( out vec4 O,  vec2 U )
{
	O = texture(iChannel0, U/iResolution.xy);
}

#define rnd(x) fract(3e4*sin((x+iTime)*mat2(1,13.51,73.37,-57.17)))
#define srnd(x) sign(2.*rnd(x)-1.)
#define T(d) texture(iChannel0, fract((U+d)/R))

void mainImage( out vec4 O,  vec2 U )
{
    vec2 R = iResolution.xy;
     if (iFrame==0) {  O = vec4(rnd(U).x<1e-4); return; }

	O = T(0.)*(1.-vec4(1,2,3,0)*1e-3);            // old track fade out

    for (int j=-1; j<=1; j++)
        for (int i=-1; i<=1; i++) {
            vec2 d = vec2(i,j);
            vec4 T = T(d);
            if (T.x==1. && srnd(U+d)==-d) O = T;  // if neighbor is head and come here
        }
}
