void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    if (iFrame < 25) {

    	fragColor = vec4(1., 1., 1., 1.);

    } else {

        vec2 z = (-iResolution.yx+2.0*gl_FragCoord.yx)/iResolution.y;

        vec2 an = 0.51*cos( vec2(0.0,1.5708) + 0.1*iTime ) - 0.25*cos( vec2(0.0,1.5708) + 0.2*iTime );

        float f = 1e20;
        for( int i=0; i<32; i++ ) 
        {
            z = vec2( z.x*z.x-z.y*z.y, 2.0*z.x*z.y ) + an;
            f = min( f, dot(z,z) );
        }

        f = 1.0+log(f)/16.0;
        f = f*f;
        if (cos(iTime*2.) > 0.)  f = 1.-f;

        vec2 vUv = fragCoord.xy / iResolution.xy;
        vec3 uv = texture(iChannel0, vUv).xyz;

        vec3 vvv = vec3(f, f, f);
        vec3 xxx = cross(vvv, vec3(uv.x*4., uv.y*4., 1.+length(uv)));

        fragColor = vec4(mix(uv, xxx, 0.075), 1.0);
    }
}
