#define pi 3.1419526535

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates
    vec2 uv = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;

    vec2 qr;


    uv = uv*1.1;

    //domain restricted to -pi/2 < x < 3pi/2
    qr = vec2(length(uv),pi/2.-atan(uv.x, uv.y));


    fragColor =  vec4(0.);



    for (float i = -pi/2.; i<=2.*pi; i+=1./100.){

        if(qr.x > cos(iTime*i/1.) -.01 && qr.x < cos(iTime*i/1.) && qr.y > i -.09 && qr.y < i){

            fragColor = vec4(1.+uv.x,1.+uv.y,sin(iTime)/2.+.5,1);
        }

    }

