void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    float t = 0.0; 
    vec2 dp;
    for ( float j = 1.; j < 4.; j++ ) {
        for ( float i = 0.; i < 1023.; i++ ) {
            dp = uv - vec2( 
                0.5 + sin( j * iTime + i * 0.01 ) * 0.1 * j, 
                0.5 + j * sin( iTime - i * 0.01 ) * 0.1
            );

            t += smoothstep( 0.02, 0.0, sqrt( dot( dp, dp ) ) );
        }
    }

    fragColor = vec4( mix( vec3( 0.3 ), vec3( 0.5426, 0.4574, 0.0 ), t ), 1.0 );
}
