const float PI = 3.1415926535897932384626433832795;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // UV -.5 -> .5
    vec2 uv = fragCoord/vec2(iResolution.y,iResolution.y)  - vec2(0.9, 0.5);

    float rsq = uv.x * uv.x + uv.y * uv.y;
    float r = sqrt(rsq);
    //float theta = atan(-uv.y, uv.x) + PI;
    // Rotate
    float theta = atan(-uv.x, -uv.y) + PI;

    float Angle = mod(iTime, 360.0) * (PI / 180.0);

    const float rsq1 = 0.11;
    const float rsq2 = 0.16;
    vec3 graycol = vec3(0.196, 0.196, 0.266);  // #323244
    vec3 greencol = vec3(0.286, 0.8078, 0.545);  // #49CE8B
    float aa_angle_edge = smoothstep(Angle - 0.02, Angle, theta);
    vec3 col = mix(greencol, graycol, 1.0 - aa_angle_edge);

    col *= vec3(smoothstep(rsq1 - 0.005, rsq1, rsq));
    col *= vec3(1.0 - smoothstep(rsq2 - 0.005, rsq2, rsq));

    // Output to screen
    fragColor.rgb = col;
    //fragColor = vec4(theta / (2.0*PI));
    //fragColor = vec4(uv.x, uv.y, 0.0, 1.0);
    fragColor.a = 1.0;

}
