void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    vec2 z = 1.0 * uv;

    vec2 mouse_norm = (2.0 * iMouse.xy - iResolution.xy) / iResolution.y;

    vec2 c = vec2(0.2, 0.4) + 0.1 * mouse_norm;

    float min_dist = min(abs(z.x), abs(z.y));

    for (int i = 0; i < 500; ++i) {
    	z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;
        if (z.x * z.x + z.y * z.y > 100.0) {
            break;
        }
        min_dist = min(min_dist, min(abs(z.x), abs(z.y)));
    }

    min_dist = 10.0 * min_dist;

	vec3 col =
        vec3(smoothstep(0.0, 0.01, min_dist),
             smoothstep(0.0, 0.05, min_dist),
             smoothstep(0.0, 0.25, min_dist));

    // Output to screen
    fragColor = vec4(col,1.0);
}
