float circle(vec2 uv, vec2 pos, float r){

    return r/distance(uv, pos);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;

    float r = .035;

    uv.x *= iResolution.x / iResolution.y;

    float c = circle(uv, vec2(sin(iTime * 2.) * .4,  cos(iTime * .4) * .4), r);
    c += circle(uv, vec2(sin(iTime * .5) * .4, cos(iTime * .7) * .4), r);
    c += circle(uv, vec2(sin(iTime * .7) * .4, cos(iTime * .8) * .4), r);
    c += circle(uv, vec2(sin(iTime * .2) * .4, cos(iTime * .3) * .4), r);
    c += circle(uv, vec2(sin(iTime * .3) * .4, cos(iTime * .4) * .4), r);
    c += circle(uv, vec2(sin(iTime * .6) * .4, cos(iTime) * .4), r);
    c += circle(uv, vec2(sin(iTime * .5) * .4, cos(iTime * .2) * .4), r);
    c += circle(uv, vec2(sin(iTime * .3) * .4, cos(iTime) * .7), r);



    // Output to screen
    fragColor = vec4(vec3(0.), 1.) + vec4(1., 1. * c / 3., 0., 1.) * c;
}
