void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float c = cos(iTime), 
          s = sin(iTime),
          uvScale = 1.;

    vec2 uv = fragCoord.xy / iResolution.xy;
	uv.y *= iResolution.y / iResolution.x;
	uv.x = mix(-1. * uvScale, uvScale, uv.x);
	uv.y = mix(-1. * uvScale, uvScale, uv.y);

    vec2 metaballs[4];
	metaballs[0] = vec2(sin(iTime * 0.5) * .5 - .23, abs(s * 0.5) - 1.);
	metaballs[1] = vec2(cos(iTime*0.7) * .4 + .1, abs(sin(iTime * .25)) * .9 - 1.);
	metaballs[2] = vec2(sin(iTime * 1.33) * .3,cos(iTime * 1.3)* .3 - .8);
	metaballs[3] = vec2(c*.5, abs(s*.9 * s*.3) - 1.);

    vec4 ballCol = vec4(
          .04 / pow(distance(uv, metaballs[0]), 2.0)
        + .01 / pow(distance(uv, metaballs[1]), 2.0)
        + .3 / pow(distance(uv, metaballs[2]), 2.0)
        + .03 / pow(distance(uv, metaballs[3]), 2.0)
	);

    vec3 oldcolor = ballCol.xyz * texture(iChannel0, (mod(fragCoord, 8.0) / 8.0)).xyz;
    vec3 dithered = floor( vec3(oldcolor.x, oldcolor.x, oldcolor.x) );
    fragColor = vec4( 1. - dithered, 1.0 );
    //fragColor = vec4( ballCol );
}
