void mainImage( out vec4 fragColor, in vec2 fragCoord)
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    //vec4 col = texture(iChannel0, vec2(uv.x+0.003,-uv.y))

    vec2 direction = uv - .5;

    //uv += length(direction);




    // Output to screen

    vec4 parta = texture(iChannel0, vec2(uv.y-iTime, -uv.x));
    vec4 partb = texture(iChannel0, vec2(uv.y-iTime, -uv.y));
    vec4 partc = texture(iChannel0, vec2(uv.x-iTime, -uv.x));
    vec4 partd = texture(iChannel0, vec2(uv.x-iTime, uv.y));


    switch(int(iTime)%4) {
        case 0:
        fragColor = parta;
        break;

        case 1:
        fragColor = partb;
        break;

        case 2:
        fragColor = partc;
        break;

        case 3:
        fragColor = partd;
        break;

        default:
        fragColor = partd;
        break;
    }

}
