#define SPEED 0.5
#define CIRCLES 20.
#define SHARPNESS 2.5


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // -1 to 1
    vec2 uv = ( 2.*fragCoord - iResolution.xy ) / iResolution.y;
    float dist = length(uv);

	float mask = sin((dist - (iTime * float(SPEED))) * CIRCLES) * SHARPNESS; 

    vec3 col;
    if (mask > 0.) {
        mask = clamp(mask, 0., 1.);
        col = mask * texture(iChannel0, fragCoord / iResolution.xy).rgb;
    } else {
        col = texture(iChannel1, fragCoord / iResolution.xy).rgb;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
