void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	mat2 rot;

	float deg = radians(iTime * 25.0);

	vec2 aspectratio = iChannelResolution[0].xy / iResolution.xy;

	rot[0] = vec2(cos(deg), sin(deg));
	rot[1] = vec2(-sin(deg), cos(deg));

	vec2 uv = fragCoord.xy*-1.0 / iResolution.xy;

	uv += vec2(.5);

	uv.xy /= aspectratio;

	float zoom = 2.0 + sin(iTime);
	uv.xy *= zoom;

	uv.xy *= rot;

	uv.xy *= aspectratio;

	uv -= vec2(.5);

	float circle = -radians(iTime * 100.0);

	uv += vec2(cos(circle), sin(circle));

	uv.y /= 1.3;

	fragColor = texture(iChannel0, uv);
}
