// Today I saw a print by Super Terrain at a fanzine festival, it inspired me this shader.

// Char lookup adapted from FabriceNeyret2's https://www.shadertoy.com/view/ltcXzs
float char(vec2 p, int C) {
    if (p.x<.1|| p.x>=.9 || p.y<.1|| p.y>=.9) return 0.;
    return smoothstep(.5,.49,textureGrad( iChannel0, p/16. + fract( vec2(C, 15-C/16) / 16. ) , 
                       dFdx(p/16.),dFdy(p/16.) ).w);
}

vec3 clr=vec3(1.);

void mask(vec2 uv, float offset) {
    uv-=.5;
    for(int i=0;i<3;++i){
        float hash=fract(sin(floor(iTime*3.)+offset+float(i)*10.+dot(floor(uv),vec2(13.,78.)))*1e4),
            c=65.+floor(hash*26.),
            m=char(fract(uv), int(c));
        clr[i] -= m;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;
    mask(uv*.6,0.);
    mask(uv*3.,1.);
    mask(uv*8.,2.);
    fragColor = vec4(clr,1.);
}
