float getToothOffset(vec3 tooth, float y){
    float TEETH_COUNT = tooth[0];
    float TOOTH_DEPTH = tooth[1];
    float ANIM = tooth[2];

    y = y + ANIM;

    return abs(fract(y * TEETH_COUNT) * 2.0 - 1.0) * TOOTH_DEPTH / iResolution.x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float GLOBAL_TOOTH_SIZE = 0.3f;
    // Uncomment to control size with mouse
    //float GLOBAL_TOOTH_SIZE = iMouse.x / iResolution.x;

    vec3 TOOTH_1 = vec3(40.0, 10.0, iTime * 0.2f);
    vec3 TOOTH_2 = vec3(3, 40, iTime * 0.31f);
    vec3 TOOTH_3 = vec3(7, 40, iTime * 0.1f);

	vec2 uv = fragCoord.xy / iResolution.xy;

    uv.x = uv.x
        + getToothOffset(TOOTH_1, uv.y) * GLOBAL_TOOTH_SIZE
        + getToothOffset(TOOTH_2, uv.y) * GLOBAL_TOOTH_SIZE
        + getToothOffset(TOOTH_3, uv.y) * GLOBAL_TOOTH_SIZE;

	fragColor = texture(iChannel0, uv);
}
