// Julia

vec2 julia = vec2(-0.79, 0.158);
 void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
 { 
     vec2 q=fragCoord.xy/iResolution.xy; 
 	 vec2 p = -1.0 + 2.0*q; 
 	 p.x*=iResolution.x/iResolution.y; 
     vec2 z=p;
    // vec2 juliac = vec2(julia.x * sin(iTime * 0.2) - 0.5, julia.y * cos(iTime * 0.2));
     float counter=0.; 
     for(int i=0;i<388;i++){ 
		 if(dot(z,z) < 1024.0) {
         	z=vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + julia; 
         	counter++;
		 }else {break;}
     } 
	 float c = counter - log(log(length(z))/log(32.)) / log(2.0);
	 vec3 col = vec3(0.02*c);

     fragColor.xyz=col; 
 }
