#define STARTANGLE ( (radians( 0.0 ) ) )
#define ENDANGLE	( (radians( 360.0 ) ) )
#define RADIUS		( 200.0 )
#define INNERRADIUS	( 100.0 )

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;

	vec2 center = iResolution.xy / 2.0;
	vec2 pc = uv - center;

	float ln = length( pc );
	vec2 dir = pc / ln;

	float angleVal = atan( dir.y, dir.x );

	float progress = -mod( iTime * 50.0 ,360.0);

	float end = radians( 180.0);
	float start = radians(progress+180.0);


	if ( angleVal >= start && angleVal <= end && ln <= RADIUS && ln >= INNERRADIUS )
	{
		float aa = abs(ln - (( RADIUS - INNERRADIUS)*0.5 + INNERRADIUS))/(RADIUS-INNERRADIUS);
		float res = smoothstep(1.0,0.0,aa * 30.0 );
		fragColor = vec4(res);
	}
	else
		fragColor = vec4(0);
}
