float rigid = 1.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec2 muv = iMouse.xy/iResolution.xy;
	vec2 muv2 = 2.0*(muv-0.5);
	rigid = length(muv2);

	vec2 uv2 = 2.0*(uv-0.5);
	float r = length(uv2);	
	float StartPos = 1.0-rigid;
	float EndPos = 1.0;

	float Value =1.0;
	if(r>StartPos)
	{
		Value = 1.0-(r-StartPos)/(EndPos-StartPos);
	}


	vec4 Color=vec4(1.0);
	Color.rgb = vec3(Value);


	fragColor = Color;


}
