#define _ 0.0
#define o (1./3.)
#define b (2./3.)
#define B 1.0

#define check(r,g,b) color=vec4(r,g,b,0.); dist = distance(s,color); if (dist < bestDistance) {bestDistance = dist; bestColor = color;}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float dist;
	float bestDistance = 1000.;
	vec4 color;
	vec4 bestColor;		

	vec2 pixel = vec2(320,200)-floor( fragCoord.xy / iResolution.xy * vec2(320,200));
	vec4 s = texture(iChannel0, pixel / vec2(320,200));

	s += (texture(iChannel1, pixel / iChannelResolution[1].xy)-0.5)*0.5;

	// pallete 0

	check(_,_,_);
	check(_,B,_);
	check(B,_,_);
	check(B,B,_);

	vec4 color1 = bestColor;
	bestDistance = 1000.;

	// pallete 1

	check(_,_,_);
	check(_,B,B);
	check(B,_,B);
	check(B,B,B);

	vec4 color2 = bestColor;

	float t = (clamp(sin(iTime)*4.,-1.,1.)+1.)/2.;

	fragColor = mix(color2, color1, t);	
}
