void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;    
	//vec2 uv = (fragCoord.xy - center.xy) / iResolution.xy;
    //uv.x *= iResolution.x/iResolution.y; //fix aspect ratio

    float border = 0.01;
    float circle_radius = 0.4;
    vec4 circle_color = vec4(1.0, 1.0, 1.0, 1.0);
    vec2 circle_center = vec2(0.5, 0.5);

    vec4 bkg_color = texture(iChannel0, uv * vec2(1.0, -1.0));

    // Offset uv with the center of the circle.
    uv -= circle_center;

    float dist =  sqrt(dot(uv, uv));

    float t = 1.0 + smoothstep(circle_radius, circle_radius+border, dist)
    - smoothstep(circle_radius-border, circle_radius, dist);

    fragColor = mix(circle_color, bkg_color,t);
}
