#define cubr(x,pos,ax)(ax[0]*((mod(x,2.)-.5))+ax[1]*(mod(floor(x/2.),2.)-.5)+ax[2]*(floor(x/4.)-.5)+pos)

void mainImage( out vec4 o,  vec2 i )
{
	vec3 ir=iResolution;
    i = i / ir.xy-.5; i.x*=ir.x/ir.y;i/=3.;
	o-= o-.1;

    vec3 pos=vec3(sin(iDate.w),cos(iDate.w),1.5);
    mat3 ax= mat3
    (
        vec3(0,cos(iDate.w),sin(iDate.w)),
        vec3(0,cos(iDate.w+acos(0.)),sin(iDate.w+acos(0.)))  ,
        vec3(1,0,0) 
    );

    for (float x=0.;x<8.;x++)//show cube
    {
        vec3 cub=3.*(cubr(x,pos,ax));
        float cubm=(cub.z/8.+4.);
        float l=length(.1*cub.xy/cubm-i);
        o+=1e-9/cubm/(pow(l,4.))-.005;//float(l<5./cubm)*
   if (x<3.)//show axes
    {  
        int c=int(x);
        vec3  apos=pos;
        apos+=ax[c];

    	for (float m=.0;m<1.;m+=.1)
    	{
        apos=mix(apos,pos,pow(m,2.5));
        float l=length(.1*apos.xy/(apos.z)-i);
    	o[c]+=5e-4/l-.0051;
        }
    }
    }
    o=mix(o*o,o,.3);o.b+=.1;
}


