void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 r = 2.0 * vec2(uv.x - 0.5, uv.y - 0.5);
    r.x *= iResolution.x / iResolution.y;
    vec3 ret = vec3(0.1);
    vec3 col = vec3(0.8, 0.4, 0.4);
    for(float i = 0.0; i >= -2.0; i -= 0.1){
    	if(length(r - vec2(cos(iTime - i) * 0.8, sin(iTime - i) * 0.8) + vec2(-cos((iTime - i) * 8.0) * 0.1, sin((iTime - i) * 8.0) * 0.1) + vec2(cos((iTime - i) * 32.0) * 0.025, sin((iTime - i) * 32.0) * 0.025)) <= 0.03){
    		ret = col;
    	}
        if(length(r + vec2(cos(iTime - i) * 0.8, sin(iTime - i) * 0.8) + vec2(-cos((iTime - i) * 8.0) * 0.1, sin((iTime - i) * 8.0) * 0.1) + vec2(cos((iTime - i) * 32.0) * 0.025, sin((iTime - i) * 32.0) * 0.025)) <= 0.03){
    		ret = col;
    	}
    }

	fragColor = vec4(ret,1.0);
}
