#define TAU 6.28318531

float rand(float x){return fract(sin(x)*1e4);}
float rds(float x){return -1.+2.*step(.5,rand(x));}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy -.5* iResolution.xy)/iResolution.y;
    float r=length(uv)*50.;
    float ri=floor(r);
    float sg=floor(10.+rand(ri)*10.0);
    float sp=rds(ri*15.)*(.1+.4*rand(ri*70.));
    float a=floor(fract(atan(uv.y,uv.x)/TAU+iTime*sp)*sg);
    float c=step(.7,rand(a+ri*40.));
    c*=smoothstep(-.9,-.85,sin(iTime*rand(ri*85.)));
    c*=step(.2,fract(r));
    c*=step(2.,ri)-step(20.,ri);
	fragColor = vec4(c);
}
