void mainImage( out vec4 fragColor , in vec2 fragCoord  )
{
    float t = iTime * .137;
    vec2 v = (fragCoord  / iResolution.y - .5) * 2.;  
    float m = mod(t * -.02 - length(v) * .137,1.);
    if(v.y < 0.) m = mod(t*.02-length(v) * .37,1.);  
    float n = smoothstep(m, m + 1.0, 1.) ;
    if(v.y <  0. ) n = 1. - n;   
    fragColor  = vec4( mix( vec3(1., .337, .11), vec3(.75, .79, .72), n), 1.);   
}
