void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 colorR = texture(iChannel0, uv);
    vec4 colorG = texture(iChannel0, vec2(uv.x - 0.02 * sin(iTime * 2.0 + uv.y * 50.0), uv.y));
    vec4 colorB = texture(iChannel0, vec2(uv.x + 0.02 * sin(iTime * 2.12 + uv.y *41.0), uv.y));
    fragColor = vec4(colorR.r, colorG.g, colorB.b, 1.0) * sin(uv.y * iResolution.y * 1.5);
}
