const vec2 center = vec2(0.0);
const float radius = 0.5;
const float warpCoeff = 8.0;
const float moveCoeff = 0.1;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 grad = vec4(dFdx(uv), dFdy(uv));
    vec2 centeredUV = uv * 2.0 - 1.0;
    vec2 v = centeredUV - center;
    float phi = atan(v.y, v.x);
    float len = length(v);
    float d = max(radius-length(v), 0.0);
    float rho = phi + d * warpCoeff;
    vec2 uvWarp = vec2(cos(rho), sin(rho)) * len * 0.5 + 0.5;
    uv = len<radius ? uvWarp : uv;
	uv.x = fract(iTime*moveCoeff+uv.x);
    vec4 col = texture(iChannel0, uv);
	fragColor = col;
}
