void mainImage( out vec4 f, in vec2 p )
{
	vec2 q = p.xy / iResolution.xy;

    vec3 x0y0 = vec3( 0.0, 0.0, 1.0 ); // Bottom Left
    vec3 x1y0 = vec3( 1.0, 0.0, 0.0 ); // Bottom Right
    vec3 x0y1 = vec3( 0.0, 1.0, 0.0 ); // Top Left
    vec3 x1y1 = vec3( 1.0, 1.0, 0.0 ); // Top Right

    vec3 x0 = mix( x0y0, x1y0, q.x );
    vec3 x1 = mix( x0y1, x1y1, q.x );
    vec3 y  = mix( x0  , x1  , q.y );

    f = vec4( y, 1.0 );
}
