void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x += atan(uv.x - 0.5, 1.0 - uv.y + 0.1);
    uv.x += sin(iTime * 10.0 + uv.y * 50.0) * (1.0 - uv.y) * 0.2;
    uv.y += sin(iTime + uv.x * 20.0) * 0.5;
    uv.x += -0.5;
    uv.x *= (uv.y + 0.5) * 2.0;
    uv.x += 0.5;
    vec4 color = texture(iChannel0, uv);
    //color *= uv.y;
    color.b = (1.0 - (fragCoord.y / iResolution.y)) * 0.3 * sin(iTime);
    color.g = 0.0;
	fragColor = color;
}
