void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 uvM = iMouse.xy / iResolution.xy;
    vec4 o = texture(iChannel1, uv);
    float noise = 1.0 - (o.x + o.y + o.z) / 3.0;
    //float m = sqrt(pow(uvM.x - 0.5, 2.0) + 
    //           pow(uvM.y - 0.5, 2.0));

    float m = iTime / 50.0;

    float range = 0.1;
    float s = smoothstep(m - range, m + range, sqrt(pow(uv.x - 0.5, 2.0) + pow(uv.y - 0.5, 2.0)));
    float v = mix(noise, s, 0.55);
    fragColor = vec4(1,1,1,1) * step(0.5, v);

    float cut = (1.0 - step(0.5, v) * step(-0.55, -v));
    fragColor.y = fragColor.y * cut;
    fragColor.z = fragColor.z * cut;
}
