// based on https://www.shadertoy.com/view/Msf3z7

#define t iTime
#define r iResolution
#define d iDate.w

float div = 1.5;
float intensity = 0.033;

float pair( float x, float y, float z, vec2 f, float blink)
{
    float dist = z * 0.06125;
    vec3 eye1 = vec3(x + dist , y, z);
	vec3 eye2 = vec3(x - dist, y, z);

    float sum = 0.;
    sum += eye1.z / distance(eye1.xy, f);
	sum += eye2.z / distance(eye2.xy, f);
	return sum * ceil(abs(blink));
}

void mainImage( out vec4 o, vec2 f )
{
	float ratio = r.y / r.x;

	f /= r.xx;

	float pair1x = sin(t * .33) * 0.25 + 0.5;
	float pair1y = cos(t * .4) * 0.25 + 0.5;
	float pair1z = sin(t * .2) * 0.1 + 0.2;
    float blink1 = sin(d * .25);

	float pair2x = sin(t * .1) * 0.25 + 0.5;
	float pair2y = cos(t * .78) * 0.25 + 0.5;
	float pair2z = cos(t * .5) * 0.1 + 0.2;
    float blink2 = sin(d * .33);

	float pair3x = sin(t * .03) * 0.25 + 0.5;
	float pair3y = cos(t * .5) * 0.25 + 0.5;
	float pair3z = cos(t * .725) * 0.1 + 0.2;
    float blink3 = sin(d * .5);

	float pair1 = pair(pair1x, pair1y * ratio, pair1z, f, blink1);
	float pair2 = pair(pair2x, pair2y * ratio, pair2z, f, blink2);
	float pair3 = pair(pair3x, pair3y * ratio, pair3z, f, blink3);

	float s = pair1 + pair2 + pair3;

    s = pow(s * intensity, div) * 0.5;

	o = vec4(s * 0.9, s * 0.05, s * 0.01, 1.0);
}
