void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(uv,0.9+0.5*sin(iTime),1.0);
    vec2 q = uv - vec2(0.5, 0.5);
    vec3 col = vec3(14.0, 5, 0.9);    
    float r = 0.5;
    col *= smoothstep(r,r+0.01, length(q));    
    fragColor = vec4(col, 4.0);    
    float music = texture( iChannel0, vec2(0.0, 0.01 ) ).x;
    float music1 = mod(4.*(music*1.), 3.0);
	float Background = (0.9+0.5) * abs(2. / (12.0 * uv.y));
    vec3 colors = vec3(q,0.6+0.4*cos(iTime));    
    fragColor = vec4(((Background) * colors * music1), 0.1);
}
