renderpass.code:
#define black	vec3( 0. )
#define grey	vec3( .8 )
#define white	vec3( 1. )
#define red		vec3( 1., 0., 0. )
#define blue	vec3( 0., 0., 1. )
#define yellow	vec3( 1., .9, 0. )
#define halfStripe .00125
#define RU false
#define DE true
#define res iResolution

// LED based on https://www.airtightinteractive.com/demos/js/ledeffect/
#define spacing 10.
#define size 2.
#define blur 3.

vec2 p = vec2(0.);
float shiftY = 0.;

vec3 flag( in bool f )
{

    vec3 col = f ? yellow : red;
    if ( p.y > 1. / 3.) col = f ? red : blue;
    if ( p.y > 2. / 3.) col = f ? black : white;

    //col -= shiftY * 2.5 ;

    if ( p.y < 0. || p.y > 1. ) col = grey;    

    return col;

}

void mainImage( out vec4 o, in vec2 f )
{
    vec2 count = res.xy / spacing;
    p = floor( f / spacing) / count;

    float ledsize = pow(size,2.);
    float ledblur = pow(blur * 2., 2.);

    shiftY = sin( iTime - tan(p.x) * 10.) * .075;

    p.y += shiftY;

    vec3 col = iMouse.w > 0.5 ? flag( DE ) : flag( RU );
    if ( p.x > 0.5 - halfStripe ) col = grey;
    if ( p.x > 0.5 + halfStripe ){ col = iMouse.w > 0.5 ? flag( RU ) : flag( DE ); }

    vec2 pos = mod(f.xy, vec2(spacing)) - vec2(spacing / 2.0);
    float dist_squared = dot(pos, pos);

    o = mix(vec4(col,1.), vec4(0.0), smoothstep(ledsize, ledsize + ledblur, dist_squared));

}
